from pytube import YouTube

# URL do vídeo do YouTube
url_video = 'https://www.youtube.com/watch?v=VIDEO_ID'

# Função para extrair os comentários
def extrair_comentarios(url):
    try:
        yt = YouTube(url)
        comentarios = yt.comments
        return comentarios
    except Exception as e:
        print("Erro ao extrair comentários:", e)
        return []

# Extrair os comentários do vídeo
comentarios = extrair_comentarios(url_video)

# Exibir os comentários
for comentario in comentarios:
    print(comentario)
